<template>
    <div class="dhCon" id="Fullscreen">
        <div class="topData">
            <div class="tLeft">
                <div class="wrap">
                    <dv-border-box-10 style="height:275px; overflow: hidden;">
                        <p>用户统计</p>
                        <div class="title">
                            <div class="titleWrap">
                                <span class="txt">货主</span>
                                <span class="totalTxt">{{consignor}}</span>
                            </div>
                            <div class="titleWrap _driver">
                                <span class="txt">司机</span>
                                <span class="totalTxt">{{driver}}</span>
                            </div>
                        </div>
                        <div
                            class
                            id="UserStatistics"
                            style="width:100%; height:250px;top: 10%;left: -10%;"
                        ></div>
                    </dv-border-box-10>
                </div>
                <div class="wrap">
                    <dv-border-box-10 style="height:275px; overflow: hidden;">
                        <p>交易统计</p>
                        <div class="title">
                            <div class="titleWrap">
                                <span class="txt">订单</span>
                                <span class="totalTxt">{{totalOrder}}</span>
                            </div>
                            <div class="titleWrap _driver">
                                <span class="txt">运单</span>
                                <span class="totalTxt">{{totalBill}}</span>
                            </div>
                        </div>
                        <div
                            class
                            id="DealStatistics"
                            style="width:335px; height:220px; position: relative; top:14%; left:-5%;"
                        ></div>
                    </dv-border-box-10>
                </div>
            </div>
            <div class="tRight">
                <div class="wrap">
                    <dv-border-box-13 style="height:565px; overflow: hidden;">
                        <map-echarts :getFreight="totalFreight" :getWeight="totalWeight"></map-echarts>
                    </dv-border-box-13>
                </div>
            </div>
        </div>
        <div class="bottomData">
            <div class="bLeft">
                <div class="wrap" :style="'height:'+bottom_h+'px'">
                    <dv-border-box-12 :style="'height:'+(bottom_h-20)+'px'">
                        <bl-echarts :bottomH="(bottom_h-60)" />
                    </dv-border-box-12>
                </div>
            </div>
            <div class="bRight">
                <div class="wrap" :style="'height:'+bottom_h+'px'">
                    <dv-border-box-12 :style="'height:'+(bottom_h-20)+'px'">
                        <br-echarts :bottomH="(bottom_h-60)" />
                    </dv-border-box-12>
                </div>
            </div>
        </div>
		<!-- 返回 -->
		<!-- <div class="pageBackBtn" @click="BackFun">返回</div> -->
    </div>
