<script>
var echarts = require('echarts');
import request from '../../utils/request.js';
import mapEcharts from '../tplChart/mapecharts.vue';
import blEcharts from '../tplChart/blecharts.vue';
import brEcharts from '../tplChart/brecharts.vue';
// import columnarCharts from '../tplChart/columnarcharts.vue';
export default {
    data() {
        return {
            angle: 0,
            consignor: 0,
            driver: 0,
            totalBill:0,
            totalOrder:0,
            deal:0, //订单运单成交比例
            totalFreight: 0,
            totalWeight: 0,
			win_h: 100,//屏幕高度
			bottom_h: 100,//顶部模块高度
            // consignorSrc: require('../../../src/assets/img/hz.png'),
            // driverSrc: require('../../../src/assets/img/siji.png'),
        };
    },
    components: { mapEcharts, blEcharts, brEcharts },
    created() {
		this.win_h = window.innerHeight;
		this.bottom_h = this.win_h - 580;
		if(this.bottom_h<250){
			this.bottom_h = 250;
		}
		this.getData();
    },
    methods: {
		fullscreenFun(){
			this.$nextTick(()=>{
				let element = document.getElementById('Fullscreen');
	                if (element.requestFullscreen) {
	                    element.requestFullscreen()
	                } else if (element.mozRequestFullScreen) {
	                    element.mozRequestFullScreen()
	                } else if (element.msRequestFullscreen) {
	                    element.msRequestFullscreen()
	                } else if (element.webkitRequestFullscreen) {
	                    element.webkitRequestFullScreen()
	                }
			})

		},
        async getData() {
            let loginData = this.$store.state.loginData;
            // console.log(loginData);
            let hd = {
                pi: 32006,
                si: loginData.si,
                sq: 9
            };
            let bd = {
                tid: loginData.tid
            };
            let resData = await request('/32006', hd, bd);
            if (resData.hd.rid >= 0) {
                let bd = JSON.parse(resData.bd);
                this.consignor = bd.totalCustom;
                this.driver = bd.totalDriver;
                this.totalBill = bd.totalBill;
                this.totalOrder = bd.totalOrder;
                this.deal = ((bd.totalBill / bd.totalOrder) * 100).toFixed(2);
                this.totalFreight = (bd.totalFreight / 10000).toFixed(2);
                this.totalWeight = (bd.totalWeight / 10000).toFixed(2);
                console.log(bd);
            } else {
                this.$message({
                    type: 'error',
                    message: resData.hd.rmsg
                });
            }
        },
        getCirlPoint(x0, y0, r, angle) {
            let x1 = x0 + r * Math.cos((angle * Math.PI) / 180);
            let y1 = y0 + r * Math.sin((angle * Math.PI) / 180);
            return {
                x: x1,
                y: y1
            };
        },
        draw(myChart, angle, option) {
            this.angle = this.angle + 3;
            myChart.setOption(option);
            
            //window.requestAnimationFrame(draw);
        },
        init() {
            this.$nextTick(() => {
                var UserChart = echarts.init(document.getElementById('UserStatistics'));
                var DealChart = echarts.init(document.getElementById('DealStatistics'));
                // 用户统计
                const res = [
                    {
                        label: '货主',
                        value: this.consignor
                    },
                    {
                        label: '司机',
                        value: this.driver
                    }
                ];
                const border =
                    'data:image/png;base64,';
                const man =
                    'data:image/png;base64,';
                const woman =
                    'data:image/png;base64,';
                let hz = this.consignorSrc;
                let sj = this.driverSrc;
                // const color = ['#00FFFF', 'rgba(196,36,255,1)'];
                const color = ['#00FFFF', '#FFD028'];
                const data = [];
                for (let i = 0; i < res.length; i++) {
                    data.push(
                        {
                            value: res[i].value,
                            name: res[i].label,
                            itemStyle: {
                                normal: {
                                    borderWidth: 5,
                                    shadowBlur: 20,
                                    borderColor: color[i],
                                    shadowColor: color[i]
                                }
                            }
                        },
                        {
                            value: res[0].value / 10,
                            name: '',
                            itemStyle: {
                                normal: {
                                    label: {
                                        show: false
                                    },
                                    labelLine: {
                                        show: false
                                    },
                                    color: 'rgba(0, 0, 0, 0)',
                                    borderColor: 'rgba(0, 0, 0, 0)',
                                    borderWidth: 0
                                }
                            }
                        }
                    );
                }
                UserChart.setOption({
                    color,
                    graphic: {
                        elements: [
                            {
                                type: 'image',
                                z: 3,
                                style: {
                                    image: border,
                                    width: 150,
                                    height: 150
                                },
                                left: 'center',
                                top: 'center',
                                position: [100, 100]
                            },
                            {
                                type: 'image',
                                z: 3,
                                style: {
                                    image: woman,
                                    width: 40,
                                    height: 70
                                },
                                left: '35%',
                                top: 'center'
                            },
                            {
                                type: 'image',
                                z: 3,
                                style: {
                                    image: man,
                                    width: 40,
                                    height: 70
                                },
                                right: '35%',
                                top: 'center'
                            }
                        ]
                    },
                    tooltip: {
                        show: false
                    },
                    toolbox: {
                        show: false
                    },
                    series: [
                        {
                            name: '',
                            type: 'pie',
                            clockWise: false,
                            radius: [85, 90],
                            hoverAnimation: false,
                            itemStyle: {
                                normal: {
                                    label: {
                                        show: true,
                                        fontSize: 14,
                                        formatter(params) {
                                            return params.name ? '' : '';
                                            // return params.name ? params.name + '\n' + params.value + '人' : '';
                                        }
                                    },
                                    labelLine: {
                                        width: 4,
                                        length: 15,
                                        length2: 15,
                                        show: false,
                                        color: '#00ffff'
                                    }
                                }
                            },
                            data
                        }
                    ]
                });

                // 成交统计
                // let angle = 0; //角度，用来做简单的动画效果的
                // let asd = ((this.totalBill / this.totalOrder) * 100).toFixed(2);
                console.log(this.deal);
                let value = this.deal;
                let option = {
                    backgroundColor: '#ffffff00',
                    title: {
                        text: '{a|' + value + '}{c|%}',
                        x: 'center',
                        y: 'center',
                        textStyle: {
                            rich: {
                                a: {
                                    fontSize: 24,
                                    color: '#29EEF3'
                                },

                                c: {
                                    fontSize: 24,
                                    color: '#ffffff'
                                    // padding: [5,0]
                                }
                            }
                        }
                    },

                    series: [
                        {
                            name: 'ring5',
                            type: 'custom',
                            coordinateSystem: 'none',
                            renderItem: (params, api) => {
                                return {
                                    type: 'arc',
                                    shape: {
                                        cx: api.getWidth() / 2,
                                        cy: api.getHeight() / 2,
                                        r: (Math.min(api.getWidth(), api.getHeight()) / 2) * 0.83,
                                        startAngle: ((0 + this.angle) * Math.PI) / 180,
                                        endAngle: ((90 + this.angle) * Math.PI) / 180
                                    },
                                    style: {
                                        stroke: '#0CD3DB',
                                        fill: 'transparent',
                                        lineWidth: 1.5
                                    },
                                    silent: true
                                };
                            },
                            data: [0]
                        },
                        {
                            name: 'ring5',
                            type: 'custom',
                            coordinateSystem: 'none',
                            renderItem: (params, api) => {
                                return {
                                    type: 'arc',
                                    shape: {
                                        cx: api.getWidth() / 2,
                                        cy: api.getHeight() / 2,
                                        r: (Math.min(api.getWidth(), api.getHeight()) / 2) * 0.83,
                                        startAngle: ((180 + this.angle) * Math.PI) / 180,
                                        endAngle: ((270 + this.angle) * Math.PI) / 180
                                    },
                                    style: {
                                        stroke: '#0CD3DB',
                                        fill: 'transparent',
                                        lineWidth: 1.5
                                    },
                                    silent: true
                                };
                            },
                            data: [0]
                        },
                        {
                            name: 'ring5',
                            type: 'custom',
                            coordinateSystem: 'none',
                            renderItem: (params, api) => {
                                return {
                                    type: 'arc',
                                    shape: {
                                        cx: api.getWidth() / 2,
                                        cy: api.getHeight() / 2,
                                        r: (Math.min(api.getWidth(), api.getHeight()) / 2) * 0.95,
                                        startAngle: ((270 + -this.angle) * Math.PI) / 180,
                                        endAngle: ((40 + -this.angle) * Math.PI) / 180
                                    },
                                    style: {
                                        stroke: '#0CD3DB',
                                        fill: 'transparent',
                                        lineWidth: 1.5
                                    },
                                    silent: true
                                };
                            },
                            data: [0]
                        },
                        {
                            name: 'ring5',
                            type: 'custom',
                            coordinateSystem: 'none',
                            renderItem: (params, api) => {
                                return {
                                    type: 'arc',
