                                    style: {
                                        stroke: '#0CD3DB',
                                        fill: 'transparent',
                                        lineWidth: 1.5
                                    },
                                    silent: true
                                };
                            },
                            data: [0]
                        },
                        {
                            name: 'ring5',
                            type: 'custom',
                            coordinateSystem: 'none',
                            renderItem: (params, api) => {
                                let x0 = api.getWidth() / 2;
                                let y0 = api.getHeight() / 2;
                                let r = (Math.min(api.getWidth(), api.getHeight()) / 2) * 0.95;
                                let point = this.getCirlPoint(x0, y0, r, 90 + -this.angle);

                                return {
                                    type: 'circle',
                                    shape: {
                                        cx: point.x,
                                        cy: point.y,
                                        r: 4
                                    },
                                    style: {
                                        stroke: '#0CD3DB', //粉
                                        fill: '#0CD3DB'
                                    },
                                    silent: true
                                };
                            },
                            data: [0]
                        },
                        {
                            name: 'ring5', //绿点
                            type: 'custom',
                            coordinateSystem: 'none',
                            renderItem: (params, api) => {
                                let x0 = api.getWidth() / 2;
                                let y0 = api.getHeight() / 2;
                                let r = (Math.min(api.getWidth(), api.getHeight()) / 2) * 0.95;
                                let point = this.getCirlPoint(x0, y0, r, 270 + -this.angle);
                                return {
                                    type: 'circle',
                                    shape: {
                                        cx: point.x,
                                        cy: point.y,
                                        r: 4
                                    },
                                    style: {
                                        stroke: '#0CD3DB', //绿
                                        fill: '#0CD3DB'
                                    },
                                    silent: true
                                };
                            },
                            data: [0]
                        },
                        {
                            name: '',
                            type: 'pie',
                            radius: ['75%', '53%'],
                            silent: true,
                            clockwise: true,
                            startAngle: 90,
                            z: 0,
                            zlevel: 0,
                            label: {
                                normal: {
                                    position: 'center'
                                }
                            },
                            data: [
                                {
                                    value: value,
                                    name: '',
                                    itemStyle: {
                                        normal: {
                                            color: {
                                                // 完成的圆环的颜色
                                                colorStops: [
                                                    {
                                                        offset: 0,
                                                        color: '#4FADFD' // 0% 处的颜色
                                                    },
                                                    {
                                                        offset: 1,
                                                        color: '#28E8FA' // 100% 处的颜色
                                                    }
                                                ]
                                            }
                                        }
                                    }
                                },
                                {
                                    value: 100 - value,
                                    name: '',
                                    label: {
                                        normal: {
                                            show: false
                                        }
                                    },
                                    itemStyle: {
                                        normal: {
                                            color: '#173164'
                                        }
                                    }
                                }
                            ]
                        },

                        {
                            name: '',
                            type: 'gauge',
                            radius: '77%',
                            center: ['50%', '50%'],
                            startAngle: 0,
                            endAngle: 359.9,
                            splitNumber: 8,
                            hoverAnimation: true,
                            axisTick: {
                                show: false
                            },
                            splitLine: {
                                length: 30,
                                lineStyle: {
                                    width: 5,
                                    color: '#061740'
                                }
                            },
                            axisLabel: {
                                show: false
                            },
                            pointer: {
                                show: false
                            },
                            axisLine: {
                                lineStyle: {
                                    opacity: 0
                                }
                            },
                            detail: {
                                show: false
                            },
                            data: [
                                {
                                    value: 0,
                                    name: ''
                                }
                            ]
                        }
                    ]
                };
                // DealChart.setOption(option);
                // var timer = ()=>{
                //     setTimeout(()=>{
                //         this.draw(DealChart, this.angle, option);
                //     },100);
                //     timer();
                // }
                
                setInterval(() => {
                    this.draw(DealChart, this.angle, option);
                }, 90);
            });
        }
    },
    watch: {
        consignor() {
            this.$nextTick(() => {
                if (this.consignor && this.deal) {
                    this.init();
                }
            });
        }
    },
    mounted() {
		// this.fullscreenFun()
        if (this.deal) {
            this.init();
        }
		let self = this;
			this.$nextTick(function () {
			document.addEventListener('keyup', function (e) {
				//此处填写你的业务逻辑即可
				if (e.keyCode == 27) {
					console.log(113);
				}
			})
		})
		// ap
		

    }
};
</script>

